import { useState } from 'react';
import Modal from 'react-modal';
import closeImg from '../../assets/close.svg';
import incomeImg from '../../assets/income.svg';
import outcomeImg from '../../assets/outcome.svg';
import { Container, TransactionTypeContainer, RadioBox } from './styles';

interface TransactionsModalProps { //pra receber as funções do App
  isOpen: boolean;
  onRequestClose: () => void;
}

export function TransactionModal({ isOpen, onRequestClose }: TransactionsModalProps){
  const [type, setType] = useState('deposit');
  return(
    <Modal 
      isOpen={isOpen}
      onRequestClose={onRequestClose}

      overlayClassName="react-modal-overlay"
      className="react-modal-content"
    >
      <button 
        type="button" 
        onClick={onRequestClose}
        className="react-modal-close"
      >
        <img src={closeImg} alt="close" title="close"/>
      </button>
      <Container>
        <h2>Cadastrar transação</h2>
        <input 
          placeholder="Título"
        />
        <input 
          type="number"
          placeholder="Valor"
        />
        <TransactionTypeContainer>
          <RadioBox 
            type="button"
            onClick={() => { setType('deposit'); }}
            isActive={type === 'deposit'}
            activeColor="green"
            >
            <img src={incomeImg} alt="Entrada" title="Entrada"/>
            Entrada
          </RadioBox>

          <RadioBox 
            type="button"
            onClick={() => { setType('withdraw'); }}
            isActive={type === 'withdraw'}
            activeColor="red"
            >
            <img src={outcomeImg} alt="Saída" title="Saída"/>
            Saída
          </RadioBox>
        </TransactionTypeContainer>
        <input 
          placeholder="Categoria"
        />
        <button type="submit">Cadastrar</button>
      </Container>

      </Modal>
  );

}
